provisionSchemaForm.schema:
{
  "type" : "object",
  "properties" : {
    "componentName" : {
      "title" : "Server Name",
      "type" : "string",
      "pattern" : "^[a-zA-Z0-9_\\-]+$",
      "maxLength" : 15,
      "validationMessage" : "Length must be 1-15 and Alphanumeric, underscore, and hyphen"
    },
    "tenant" : {
      "title" : "Tenant ID",
      "type" : "string",
      "default" : "76303777-3662-4b9a-a1f4-1bd291910317"
    },
    "subscription" : {
      "title" : "Subscription ID",
      "type" : "string",
      "default" : "16008187-023d-4f67-aaad-3e5943039c5f"
    },
    "client" : {
      "title" : "Client ID",
      "type" : "string",
      "default" : "1da72e3e-9432-45ad-91a2-a92f16bf7f6e"
    },
    "clientSecret" : {
      "title" : "Client Secret",
      "type" : "string",
      "default" : ""
    },
    "resourceGroup" : {
      "title" : "Resource Group",
      "type" : "string"
    },
    "location" : {
      "title" : "Region",
      "type" : "string",
      "default" : "southeastasia"
    },
    "deploymentName" : {
      "title" : "Deployement Name",
      "type" : "string"
    },
    "dnsLabelPrefix" : {
      "title" : "DNS Prefix",
      "type" : "string",
      "pattern" : "^[a-z][a-z0-9-]{1,61}[a-z0-9]$",
      "maxLength" : 80,
      "validationMessage" : "Length must be 1-80 and Alphanumeric, dash, underscore, and period"
    },
    "vmSize" : {
      "title" : "VM Size",
      "type" : "string",
      "default" : "Standard_A0",
      "enum" : [ "Standard_A0", "Standard_A1", "Standard_A2", "Standard_A3", "Standard_A4", "Standard_A5", "Standard_A6", "Standard_A7", "Standard_D1", "Standard_D2", "Standard_D3", "Standard_D4", "Standard_D11", "Standard_D12", "Standard_D13", "Standard_D14", "Standard_F1", "Standard_F2", "Standard_F4", "Standard_F8", "Standard_F16" ]
    },
    "osVer" : {
      "title" : "OS version",
      "type" : "string",
      "default" : "6.8",
      "enum" : [ "6.7", "6.8", "7.2", "7.3" ]
    },
    "adminUsername" : {
      "title" : "User Name",
      "type" : "string",
      "pattern" : "^(?!adm$)(?!admin$)(?!audio$)(?!backup$)(?!bin$)(?!cdrom$)(?!crontab$)(?!daemon$)(?!dialout$)(?!dip$)(?!disk$)(?!fax$)(?!floppy$)(?!fuse$)(?!games$)(?!gnats$)(?!irc$)(?!kmem$)(?!landscape$)(?!libuuid$)(?!list$)(?!lp$)(?!mail$)(?!man$)(?!messagebus$)(?!mlocate$)(?!netdev$)(?!news$)(?!nobody$)(?!nogroup$)(?!operator$)(?!plugdev$)(?!proxy$)(?!root$)(?!sasl$)(?!shadow$)(?!src$)(?!ssh$)(?!sshd$)(?!staff$)(?!sudo$)(?!sync$)(?!sys$)(?!syslog$)(?!tape$)(?!tty$)(?!users$)(?!utmp$)(?!uucp$)(?!video$)(?!voice$)(?!whoopsie$)(?!www-data$)[a-zA-Z0-9_\\-]+$",
      "maxLength" : 64,
      "validationMessage" : "Length must be 1-64 and User name cannot contain reserved words or special characters \\/\"\"[]:|<>+=;,?*@ or end with '.'"
    },
    "adminPassword" : {
      "title" : "Password",
      "type" : "string",
      "pattern" : "^(?=.*\\d)(?=.*[!@#$%^&*-_+=[\\]{}|:.,.?/`~.();])(?=.*[a-z])(?=.*[A-Z]).{6,64}$",
      "validationMessage" : "Length must be 6-64 and Alphanumeric, underscore, and hyphen. Password must have 3 of the following: 1 lower case character, 1 upper case character, 1 number, and 1 special character."
    },
    "provider" : {
      "type" : "string",
      "default" : "Azure"
    },
    "osName" : {
      "type" : "string",
      "default" : "RHEL"
    }
  },
  "required" : [ "componentName", "subscription", "tenant", "client", "clientSecret", "location", "deploymentName", "vmSize", "osVer", "adminUsername", "adminPassword", "provider", "osName" ]
}

